2.贪心策略

&nbsp;

a^b %mod
a*a* ... *a
偶数时
2^6 = 2 * 2 * 2 * 2 * 2 * 2;
2^6 = (2 * 2 * 2)^2
2^6 = 2^3)^2;
a^b = a^(b/2) * a^(b/2)

奇数时

2^5 = 2 * 2 * 2 * 2 * 2
2^5 = 2 * (2^2 * 2^2)
a^b = a * a ^ (b-1)
a^b = a * a ^ b

&nbsp;
<pre class="lang:c++ decode:true ">#include &lt;cstdio&gt;
#include &lt;iostream&gt;
#include &lt;cmath&gt;
#include &lt;string&gt;
//快速幂
using namespace std;
typedef long long ll;
ll npow(ll a, ll b,ll mod)
{
    //a^b %mod
    ll ans = 1;
    if(b == 0){return 1;}
    for(int i = 1; i &lt;= b; i++)
    {
        ans *= a;
        ans %= mod;
    }
    return ans;
}
ll qpow (ll a, ll b, ll mod)
{
    ll ans = 1;
    while(b &gt; 0)
    {
        if(b &amp; 1)
        {
            ans = ans * a % mod;
        }
        a = a * a % mod;
        b &gt;&gt;= 1;
    }
    return ans;

}
int main()
{
    ll a, b, mod;
    scanf("%lld %lld %lld", &amp;a, &amp;b, &amp;mod);
    cout &lt;&lt; "npow的结果如下：" &lt;&lt; '\n';
    printf("%lld\n",npow(a, b, mod));
    cout &lt;&lt; "qpow的结果如下：" &lt;&lt; '\n';
    printf("%lld\n", qpow(a, b, mod));
}</pre>
